import React from 'react'
import styled from 'styled-components'


interface Props {
  className?: string 
}

interface FlagProps{
  className?: string
  color?: string
  text: string
}
let Flag: React.FC<FlagProps> = ({ className, color, text }) => {
  return (
    <a className={className}>
       {text} 
    </a>
  )
}

Flag = styled(Flag)`
  background: ${(props) => props.color? `var(${props.color})`: "var(--green)"};
  padding: 8px 20px;
  color: white;
  border-radius: 20px

`





let  Forecasts: React.FC<Props> = ({className}) => {
  const flags: any[] = [
    {
      text: "today",
      color: "--blue"
    },
    {
      text: "month",
      color: "--green"
    },
    {
      text: "today",
      color: "--teal"
    },
  ]
  return (
    <section className={className}>
      <div>
        <div>
          <span>Forecasts since</span>
          {flags.map((flag, index) => (
            <Flag
              text={flag.text}
              color={flag.color}
              key={index}
            />)
          )}
        </div>
        <div className="chart">

        </div>
      </div>
      <div>

      </div>
    </section>
  )
}


Forecasts = styled(Forecasts)`
  height: 498px;
  background: var(--grey-light);
  box-shadow: var(--shadow-sm);
  width: 100%;
  margin-top: 78px;
  border-radius: 10px;
  padding: 30px;

  & .chart{
    width: 694px;
    height: 100%;
    background: white;
    box-shadow: var(--shadow-lg);
  }
`

export default  Forecasts
