import fetch from  "node-fetch"


const tokenUrl = "api.awhere.com/oauth/token"
const baseApiUrl = process.env.BASE_API_URL
const forecastsUrl = (lat, lon) => `${baseApiUrl}/weather/locations/${lat},${lon}/forecasts`
const dailyWeatherUrl = (lat, lon) => `${baseApiUrl}/weather/locations/${lat},${lon}/observations`


export const getForecasts = async (lat, lon) => {
  const access_token = getAccessToken();
  const url = forecastsUrl(lat, lon);
  const req = await fetch(url, {
    headers: {
      Authorization: `Bearer ${access_token}`
    }
  })
  const res = await req.json()
  return res
}


export const getDailyWeather = async (lat, lon) => {
  const access_token = getAccessToken();
  const url = dailyWeatherUrl(lat, lon);
  const req = await fetch(url, {
    headers: {
      Authorization: `Bearer ${access_token}`
    }
  })
  const res = await req.json()
  return res
}






const getAccessToken = async () => {
  const api_key = process.env.AWHERE_KEY;
  const req = await fetch(tokenUrl, {
    method: "POST",
    body: JSON.stringify("grant_type=client_credentials"),
    headers: {
      "Content-Type": "application/x-www-form-urlencoded",
      Authorization: `Basic ${api_key}`
    }
  })
  const res  = await req.json()
  return res.access_token
}
