import React from 'react'
import styled from 'styled-components'
import LogoGreen from '../assets/logo-green.svg'
import FooterInfo from './FooterInfo'
import UpArrow from '../assets/up.svg'

interface Props {
  className?: string 
}



const DownloadButton = styled.a`
  background: var(--dark-green);
  color: white;
  padding: 1rem 5rem;
  border-radius: 10px;
  display: inline-block;
  &:last-child{
    margin-bottom: auto;
  }

`





let Footer: React.FC<Props> = ({ className }) => {

  const footerSections: any[] = [
    {
      title: "Commnunity",
      elements: ["Create account", "Go premium", "Refer a freind", "Get coupon code"]
    },
    {
      title: "Support",
      elements: ["terms and conditions", "privacy & policy", "copyright issue", "Get help"]
    },
    {
      title: "Join Us",
      elements: ["Become a member", "Join agrilink", "Build your own software", "Partnership"]
    },
  ]
  return (
    <section className={className}>

      <div className="footer container">
        <div className="logo">
          <LogoGreen/>
        </div>
        {
          footerSections.map((section, idx) => (
            <FooterInfo
              title={section.title}
              elements={section.elements}
            />
          ))
        }
        <div className="download">
          <h3>Download App</h3>
          {
            ["Apple IOS", "Google play"].map((el, idx) => (
              <DownloadButton key={idx}>{ el}</DownloadButton>
            ))
          } 
        </div>
      </div>
      <div className="copyright container">
        <span>
          Copyright 2020 AbundeSEG 
        </span>
        <span>
          Go to top
          <UpArrow/>
        </span>
      </div>
    </section>
  )
}


Footer = styled(Footer)`
  .footer{
    display: grid;
    grid-template-columns: repeat(5, 1fr);
    color: var(--grey);
  }

  .download{
    display: flex;
    flex-direction: column;
    justify-content: space-between;
  }

  .copyright {
    background: var(--green);
    color: white;
    padding: 1rem 0px;
    display: flex;
    justify-content: space-between;
    & span{
      display: inline-block;

    }
  }



`

export default Footer
