import React from 'react'
import styled from 'styled-components'


interface Props {
  className?: string 
  title: string
  description: string
  inverted?: boolean
}

let AviseryButton = styled.a`
  padding: 15px 44px;
  background: ${props => props.inverted ? "white;": "var(--dark-green);"};
  color: ${props => props.inverted ? "var(--green);": "white;"};

`



let  AdviseryItem: React.FC<Props> = ( {className, title, description, inverted=false }) => {
  return (
    <div className={className}>
      <h3>{ title}</h3>
      <p>{description }</p>
      <AviseryButton
        inverted={inverted}
      >
        Know More
      </AviseryButton>
    </div>
  )
}


AdviseryItem = styled(AdviseryItem)`
  box-shadow: var(--shadow-md);
  width: 33%;
  color: ${props => props.inverted ? "var(--grey-dark);": "white;"};
  background: ${props => props.inverted ? "white;" : "var(--dark-green)"};
  border-radius: 10px;


  &::after{
    content: "";
    position: absolute;
    background: ${props => props.inverted ? "white;" : "var(--dark-green)"};
    width: 70%;
    height: 20px;
    top: 50px;
    left: 0;
  }

  p {
    font-size: 18px;
    font-weight: 500;
    color: ${props => props.inverted ? "white": "var(--dark-grey)"}
  }
`



export default  AdviseryItem
