import React,{useState, useEffect, useRef} from 'react'
import styled from 'styled-components'
import mapboxgl from 'mapbox-gl';



// const mapboxAccessToken = process.env.MAP_ACCESS_TOKEN
const mapboxAccessToken = "pk.eyJ1IjoiZXRhYmFsaW9uZWwiLCJhIjoiY2tobmkxcmp5MDIxcjJ1cGVtcnoxbTRnayJ9.F8FUtpO8FsYJFYhWxfAr1Q"

interface Props {
  className?: string 
}

let MapModal: React.FC<Props> = ({ className }) => {


  const [state, setState] = useState(
    {
      lat: "7.369722",
      lon: "12.354722",
      zoom: 0.9
    })
  const mapContainer = useRef(null)

  useEffect(() => {
    mapboxgl.accessToken = mapboxAccessToken
    console.log({ mapboxAccessToken, mapboxaccess: mapboxgl.accessToken})
    const map = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [state.lon, state.lat],
      zoom: state.zoom
    });
    map.on('move', () => {
      setState({
        lon: map.getCenter().lng.toFixed(4),
        lat: map.getCenter().lat.toFixed(4),
        zoom: map.getZoom().toFixed(2)
      });
    });
  },
  [])


  return (
    <div className={className}>
      <div id="map" ref={mapContainer}>

      </div>
    </div>
  )
}


MapModal = styled(MapModal)`
  display: grid;
  place-items: center;
  width: 100vw;
  height: 100vh;
  background: var(--grey-dark);
  opacity: 1;
  position: absolute;
  top: 0;
  left: 0;

  #map{
    width: 65%;
    height: 60%;
    background: white;
    border-radius: 10px;
    opacity: 1;

  }

`

export default  MapModal
